mapper.select:
SELECT T.* FROM system_user T WHERE 1=1

    AND T.cUserid = #{cUserid}


    AND T.cLoginname = #{cLoginname}


    AND T.cUsername = #{cUsername}


    AND T.cPassword = #{cPassword}


    AND T.cRylb = #{cRylb}


    AND T.cRyzt = #{cRyzt}


    AND T.cZcfs = #{cZcfs}


    AND T.cZjhm = #{cZjhm}


    AND T.cSex = #{cSex}


    AND T.cPhone = #{cPhone}


    AND T.cWxhm = #{cWxhm}


    AND T.cQq = #{cQq}


    AND T.cEmail = #{cEmail}


    AND T.cZt = #{cZt}

mapper.update:
UPDATE system_user
cUsername = #{cUsername}, cPassword = #{cPassword}, cZjlx = #{cZjlx}, cZjhm = #{cZjhm}, cSex = #{cSex}, cJtzz = #{cJtzz}, cPhone = #{cPhone}, cWxhm = #{cWxhm}, cQq = #{cQq}, cXguser = #{cXguser}, dXgsj = #{dXgsj},
WHERE cUserid = #{cUserid}
