mapper.delete:

delete from t_green_rw_rwmx
where c_userid = #{cUserid,jdbcType=VARCHAR}
  and c_rwlb = #{cRwlb,jdbcType=VARCHAR}
  and c_rwday = #{cRwday,jdbcType=VARCHAR}

mapper.update:

update t_green_rw_rwmx
set c_rwmouth = #{cRwmouth,jdbcType=VARCHAR},
  d_rwsj = #{dRwsj,jdbcType=TIMESTAMP},
  c_bz = #{cBz,jdbcType=VARCHAR},
  c_zt = #{cZt,jdbcType=VARCHAR},
  c_cjuser = #{cCjuser,jdbcType=VARCHAR},
  d_cjsj = #{dCjsj,jdbcType=TIMESTAMP},
  c_xguser = #{cXguser,jdbcType=VARCHAR},
  d_xgsj = #{dXgsj,jdbcType=TIMESTAMP}
where c_userid = #{cUserid,jdbcType=VARCHAR}
  and c_rwlb = #{cRwlb,jdbcType=VARCHAR}
  and c_rwday = #{cRwday,jdbcType=VARCHAR}

mapper.select:

select c_userid, c_rwlb, c_rwday, c_rwmouth, d_rwsj, c_bz, c_zt, c_cjuser, d_cjsj, 
c_xguser, d_xgsj
from t_green_rw_rwmx
where c_userid = #{cUserid,jdbcType=VARCHAR}
  and c_rwlb = #{cRwlb,jdbcType=VARCHAR}
  and c_rwday = #{cRwday,jdbcType=VARCHAR}

SELECT T.* FROM t_green_rw_rwmx T WHERE 1=1

  AND T.c_userid = #{cUserid}


  AND T.c_rwlb = #{cRwlb}


  AND T.c_rwday = #{cRwday}


  AND T.c_rwmouth = #{cRwmouth}


  AND T.d_rwsj = #{dRwsj}


  AND T.c_cjuser = #{cCjuser}

ORDER BY T.c_rwlb DESC
